const express = require('express');
const cors = require('cors');
const rateLimit = require('../../backend/node_modules/express-rate-limit/dist/index.d.cts');
const { CORS_ORIGIN, RATE_LIMIT_WINDOW, RATE_LIMIT_MAX } = require('../../backend/config/config');
const apiRoutes = require('../../routes/apiRoutes');
const errorHandler = require('../../backend/middlewares/errorHandler');

const app = express();

// Rate limiting
const limiter = rateLimit({
  windowMs: RATE_LIMIT_WINDOW,
  max: RATE_LIMIT_MAX
});

app.use(limiter);
app.use(express.json());
app.use(express.urlencoded({ extended: true }));

// CORS configuration
app.use(cors({
  origin: CORS_ORIGIN,
  methods: ['GET', 'POST'],
  allowedHeaders: ['Content-Type', 'Authorization']
}));

// API routes
app.use('/api', apiRoutes);

// Error handling middleware
app.use(errorHandler);

module.exports = app;

import React, { useState } from 'react';
import UrlInput from './components/UrlInput';
import ImageList from './components/ImageList';
import './App.css';

function App() {
  const [images, setImages] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  return (
    <div className="container">
      <h1>AI-Powered Image ALT Tag Generator</h1>
      <UrlInput 
        onImagesFetched={(imgs) => setImages(imgs)}
        setIsLoading={setIsLoading}
        setError={setError}
      />
      {error && <div className="error">{error}</div>}
      <ImageList 
        images={images} 
        setImages={setImages}
        isLoading={isLoading}
      />
    </div>
  );
}

export default App;
